<?php/* Соединяемся с базой данных */$hostname = "localhost"; // название/путь сервера, с MySQL$username = "root"; // имя пользователя (в Denwer`е по умолчанию "root")$password = ""; // пароль пользователя (в Denwer`е по умолчанию пароль отсутствует, этот параметр можно оставить пустым)$dbName = "manager"; // название базы данных/* Создаем соединение */mysql_connect($hostname, $username, $password) or die ("Не могу создать соединение");mysql_query('SET NAMES UTF8');mysql_select_db($dbName) or die (mysql_error());?>