<?phpif ( $cnt_pages > 1 ){    echo '<br /><center><div>&nbsp;Страницы: ';    if ( $page > 3 )        $startpage = '<a href="'.$_SERVER['PHP_SELF'].'?page=1"><<</a> ... ';    else        $startpage = '';    if ( $page < ($cnt_pages - 2) )        $endpage = ' ... <a href="'.$_SERVER['PHP_SELF'].'?page='.$cnt_pages.'">>></a>';    else        $endpage = '';    if ( $page - 2 > 0 )        $page2left = ' <a href="'.$_SERVER['PHP_SELF'].'?page='.($page - 2).'">'.($page - 2).'</a> | ';    else        $page2left = '';    if ( $page - 1 > 0 )        $page1left = ' <a href="'.$_SERVER['PHP_SELF'].'?page='.($page - 1).'">'.($page - 1).'</a> | ';    else        $page1left = '';    if ( $page + 2 <= $cnt_pages )        $page2right = ' | <a href="'.$_SERVER['PHP_SELF'].'?page='.($page + 2).'">'.($page + 2).'</a>';    else        $page2right = '';    if ( $page + 1 <= $cnt_pages )        $page1right = ' | <a href="'.$_SERVER['PHP_SELF'].'?page='.($page + 1).'">'.($page + 1).'</a>';    else        $page1right = '';    echo $startpage.$page2left.$page1left.'<strong>'.$page.'</strong>'.$page1right.$page2right.$endpage;    echo '</div></center>';}?>